Dice Combinations 
Dice Combinations#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for(int i = a; i&lt;=b; i++)#define rev(i, a, b) for(int i = a; i&gt;=b; i--)#define tomax(a,b) (a)=max((a),(b))#define tomin(a,b) (a)=min((a),(b))#define pb push_back#define eb emplace_back#define all(a) a.begin(), a.end()#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0);using namespace std;const int mod = 1e9 + 7;signed main(){    ios;    int n, dp[1000006], pre = 1;    cin &gt;&gt; n;    fill(dp, dp+n+3, 0);    rep(i, 1, 6){        dp[i] = pre;        pre += dp[i];    }rep(i, 7, n){        rep(j, i-6, i-1)            dp[i]+=dp[j], dp[i]%=mod;    }cout &lt;&lt; dp[n] &lt;&lt; '\n';    return 0;}// 1 1 1 1 1 1 2// 1 1 1 1 1 2 1
Minimizing Coins 
Minimizing Coins#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for(int i = a; i&lt;=b; i++)#define rev(i, a, b) for(int i = a; i&gt;=b; i--)#define tomax(a,b) (a)=max((a),(b))#define tomin(a,b) (a)=min((a),(b))#define pb push_back#define eb emplace_back#define all(a) a.begin(), a.end()#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0);using namespace std;const int MAX = 1e9;signed main(){    ios;    int n, x, dp[1000006], c[102];    cin &gt;&gt; n &gt;&gt; x;    fill(dp, dp+x+1, MAX);    dp[0]=0;    rep(i, 0, n-1){        cin &gt;&gt; c[i];        for (int j = 0; j+c[i] &lt;= x; j++){            dp[j+c[i]] = min(dp[j+c[i]], dp[j]+1);        }    }    if(dp[x]!=MAX) cout &lt;&lt; dp[x] &lt;&lt; '\n';    else cout &lt;&lt; "-1\n";    return 0;}
Coin Combinations I 
Coin Combinations I#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for(int i = a; i&lt;=b; i++)#define rev(i, a, b) for(int i = a; i&gt;=b; i--)#define tomax(a,b) (a)=max((a),(b))#define tomin(a,b) (a)=min((a),(b))#define pb push_back#define eb emplace_back#define all(a) a.begin(), a.end()#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0);using namespace std;const int mod = 1e9+7;signed main(){    ios;    int n, x, dp[1000006], c[102];    cin &gt;&gt; n &gt;&gt; x;    memset(dp, 0, sizeof(dp));    dp[0]=1;    rep(i, 0, n-1) cin &gt;&gt; c[i];    rep(i, 1, x){        rep(j, 0, n-1)            if(i-c[j]&gt;=0)                dp[i]+=dp[i-c[j]], dp[i]%=mod;                //æ¯å€‹æœ€å¤šç”¨iæ¬¡åŠ åˆ°dp[i]çš„æ–¹æ³•æ•¸    }    cout &lt;&lt; dp[x] &lt;&lt; '\n';    return 0;}
Coin Combinations II 
Coin Combinations II#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for(int i = a; i&lt;=b; i++)#define rev(i, a, b) for(int i = a; i&gt;=b; i--)#define tomax(a,b) (a)=max((a),(b))#define tomin(a,b) (a)=min((a),(b))#define pb push_back#define eb emplace_back#define all(a) a.begin(), a.end()#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0);using namespace std;const int mod = 1e9+7;signed main(){    ios;    int n, x, dp[1000006], c[102];    cin &gt;&gt; n &gt;&gt; x;    memset(dp, 0, sizeof(dp));    dp[0]=1;    rep(i, 0, n-1){        cin &gt;&gt; c[i];        for (int j = 0; j+c[i] &lt;= x; j++){            dp[j+c[i]]+=dp[j], dp[j+c[i]]%=mod;            //ç”¨ç¬¬0~iå€‹æ•¸å­—åŠ åˆ°ä»»æ„å¯é”çš„çµ„åˆæ•¸        }    }    cout &lt;&lt; dp[x] &lt;&lt; '\n';    return 0;}
Removing Digits 
Removing Digits#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define pob pop_back#define pb push_back#define eb emplace_back#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)using namespace std;int step[1000006];int get_step(int num) {    if (num &lt; 10) return 1;    if (step[num] != INT_MAX) return step[num];    for (int dig = num % 10, pos = 1; pos &lt;= num;         pos *= 10, dig = (num % (pos * 10)) / pos) {        if (num - dig != num) tomin(step[num], get_step(num - dig) + 1);    }    return step[num];}signed main() {    ios;    int n;    cin &gt;&gt; n;    fill(step, step + n + 1, INT_MAX);    cout &lt;&lt; get_step(n) &lt;&lt; '\n';    return 0;}
Grid Paths 
Grid Paths#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int mod = 1e9 + 7;int dp[1003][1003], n;bool vis[1003][1003];int dfs(int i, int j) {    // cout &lt;&lt; i sp j sp vis[i][j] &lt;&lt; '\n';    if (vis[i][j]) return dp[i][j];    if (i == n &amp;&amp; j == n) return dp[i][j] = 1;    vis[i][j] = 1;    dp[i][j] = (dfs(i + 1, j)%mod + dfs(i, j + 1)%mod)%mod;    return dp[i][j];}signed main() {    ios;    char tmp;    cin &gt;&gt; n;    memset(vis, 1, sizeof(vis));    rep(i, 1, n) rep(j, 1, n) cin &gt;&gt; tmp, vis[i][j] = (tmp != '.');    cout &lt;&lt; dfs(1, 1)%mod &lt;&lt; '\n';    return 0;}
Book Shop 
Book Shop#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)signed main() {    ios;    int n, x;    cin &gt;&gt; n &gt;&gt; x;    vii book(n);    vi dp(x + 1, 0);    for (pii &amp;i : book) cin &gt;&gt; i.F;    for (pii &amp;i : book) cin &gt;&gt; i.S;    for (pii i : book) {        rev(j, x, i.F) { tomax(dp[j], dp[j - i.F] + i.S); }    }    cout &lt;&lt; dp[x] &lt;&lt; '\n';    return 0;}
Array Description 
Array Description#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int mod = 1e9 + 7;int n, m;int dp[100005][105], arr[100005];signed main() {    ios;    cin &gt;&gt; n &gt;&gt; m;    rep(i, 1, n) cin &gt;&gt; arr[i];    if (!arr[1]) rep(i, 1, m) dp[1][i] = 1;    else dp[1][arr[1]] = 1;    rep(i, 2, n) {        if (!arr[i]) {            rep(j, 1, m) {                dp[i][j] = (dp[i][j] + dp[i - 1][j - 1]) % mod;                dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod;                dp[i][j] = (dp[i][j] + dp[i - 1][j + 1]) % mod;            }        } else {            dp[i][arr[i]] = (dp[i][arr[i]] + dp[i - 1][arr[i] - 1]) % mod;            dp[i][arr[i]] = (dp[i][arr[i]] + dp[i - 1][arr[i]]) % mod;            dp[i][arr[i]] = (dp[i][arr[i]] + dp[i - 1][arr[i] + 1]) % mod;        }    }    int ans = 0;    rep(j, 1, m) ans = (ans + dp[n][j]) % mod;    cout &lt;&lt; ans &lt;&lt; '\n';    return 0;}
Counting Towers 
Counting Towers#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int mod = 1e9 + 7;int dp[1000006][4]; // -- , oo, ox, xo (ox = xo)int n, t;void build() {    dp[1][0] = dp[1][1] = dp[1][2] = 1;    // dp[i][0] = dp[i-1][0]+dp[i-1][1]    // dp[i][1] = dp[i-1][0]+dp[i-1][1]*3    rep(i, 2, 1e6){        dp[i][0] = (dp[i-1][0]*2 + dp[i-1][1])%mod;        dp[i][1] = (dp[i-1][0]+dp[i-1][1]*4)%mod;    }}signed main() {    ios;    build();    cin &gt;&gt; t;    while (t--) {        cin &gt;&gt; n;        cout &lt;&lt; (dp[n][0] + dp[n][1])%mod &lt;&lt; '\n';    }    return 0;}
