Projects#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int bit[400005];int n;void modify(int pos, int val) {    for (; pos &lt;= 2 * n+1; pos += (pos &amp; -pos))        tomax(bit[pos], val);}int query(int pos) {    int ans = 0;    for (; pos &gt; 0; pos -= (pos &amp; -pos)) tomax(ans, bit[pos]);    return ans;}signed main() {    ios;    cin &gt;&gt; n;    vector&lt;pair&lt;pii, int&gt;&gt; p(n);    vi ord;    for (auto &amp;i : p)        cin &gt;&gt; i.F.F &gt;&gt; i.F.S &gt;&gt; i.S, ord.eb(i.F.F), ord.eb(i.F.S);    sort(all(p));    sort(all(ord));    for (auto &amp;i : p) {        i.F.F = (int)(lower_bound(all(ord), i.F.F) - ord.begin())+1;        i.F.S = (int)(lower_bound(all(ord), i.F.S) - ord.begin())+1;        modify(i.F.S, query(i.F.F - 1) + i.S);        // err(i.F.F sp i.F.S sp query(i.F.F - 1) sp query(i.F.S));    }    cout &lt;&lt; query(2 * n+1) &lt;&lt; '\n';    return 0;}
