Elevator Rides#include &lt;bits/stdc++.h&gt;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define rep(i, a, b) for(int i = a; i&lt;=b; ++i)#define rev(i, a, b) for(int i = a; i&gt;=b; --i)#define tomax(a, b) (a)=max((a),(b))#define tomin(a, b) (a)=min((a),(b))#define all(a) a.begin(), a.end()#define pb push_back#define eb emplace_back#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)using namespace std;void bit_out(int num, int digit, bool reverse){    if(!reverse)        rev(i, digit-1, 0)            cout &lt;&lt; ((num &amp; (1&lt;&lt;i))!=0) &lt;&lt; ' ';    else rep(i, 0, digit-1)            cout &lt;&lt; ((num &amp; (1&lt;&lt;i))!=0) &lt;&lt; ' ';    cout &lt;&lt; '\n';}// dp[i][0] : minimum rides// dp[i][1] : minimum weight// init// dp[i][0] = n// dp[i][1] = INT_MAX// dp[0][0] = 1, dp[0][1] = 0// new_weight = dp[i^(1&lt;&lt;j)][1] + w[j]              , j in i// dp[i][0] = min(dp[i^(1&lt;&lt;j)][0] + new_weight&gt;x)   , j in i// dp[i][1] = dp[i][0]_update ? min(dp[i][1], new_weight) : dp[i][1]    , dp[i][0]==dp[i][0]_ori &amp;&amp; new_weight&lt;=x// dp[i][1] = dp[i][0]_update ? new_weight : dp[i][1]                   , dp[i][0]&lt;dp[i][0]_ori &amp;&amp; new_weight&lt;=x// dp[i][1] = dp[i][0]_update ? w[j] : dp[i][1]                         , dp[i][0]&lt;=dp[i][0]_ori &amp;&amp; new_weight&gt;xint dp[1&lt;&lt;21][3];int n, w[22], x;signed main(){    ios;    cin &gt;&gt; n &gt;&gt; x;    rep(i, 0, n-1) cin &gt;&gt; w[i];    rep(i, 0, 1&lt;&lt;n)        dp[i][0]=n, dp[i][1]=INT_MAX;    dp[0][0] = 1, dp[0][1] = 0;    rep(i, 1, (1&lt;&lt;n)-1){        rep(j, 0, n-1){            if(i &amp; (1&lt;&lt;j)){                int new_weight = (dp[i^(1&lt;&lt;j)][1]) + w[j];                if(dp[i][0] &gt;= dp[i^(1&lt;&lt;j)][0]+(new_weight&gt;x)){                    if(dp[i][0] == dp[i^(1&lt;&lt;j)][0] &amp;&amp; (new_weight&lt;=x))                        dp[i][1] = min(dp[i][1], new_weight);                    else dp[i][1] = new_weight&gt;x ? w[j] : new_weight;                    dp[i][0] = dp[i^(1&lt;&lt;j)][0]+(new_weight&gt;x);                }            }        }//bit_out(i, n, 1), cout &lt;&lt; dp[i][0] &lt;&lt; ' ' &lt;&lt; dp[i][1] &lt;&lt; '\n';    }    cout &lt;&lt; dp[(1&lt;&lt;n)-1][0] &lt;&lt; '\n';    return 0;}
