Counting Tilings#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int mod = 1e9 + 7;int dp[11][1003][1 &lt;&lt; 11];signed main() {    ios;    int n, m;    cin &gt;&gt; n &gt;&gt; m;    memset(dp, 0, sizeof(dp));    dp[n][0][0] = 1;    rep(i, 1, m) {        rep(j, 0, (1 &lt;&lt; n) - 1) dp[0][i][j &lt;&lt; 1] = dp[n][i - 1][j];        rep(j, 1, n) {            int x = 1 &lt;&lt; (j - 1);            int y = 1 &lt;&lt; j;            rep(set, 0, (1 &lt;&lt; (n + 1)) - 1) {                dp[j - 1][i][set] %= mod;                if ((set &amp; x) &amp;&amp; (set &amp; y)) continue;                if (set &amp; x)                    dp[j][i][set ^ x] += dp[j - 1][i][set];                else if (set &amp; y)                    dp[j][i][set ^ y] += dp[j - 1][i][set];                else {                    dp[j][i][set ^ x] += dp[j - 1][i][set];                    dp[j][i][set ^ y] += dp[j - 1][i][set];                }            }        }    }    cout &lt;&lt; dp[n][m][0] % mod &lt;&lt; '\n';    return 0;}
