Distinct Values Queries#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int n, q, s;struct Q {    int l, r, id, ans;    bool operator&lt;(const Q &amp;b) const {        if (l / s != b.l / s) return l &lt; b.l;        return ((l / s) &amp; 1) ? (r &lt; b.r) : (r &gt; b.r);    }};signed main() {    ios;    cin &gt;&gt; n &gt;&gt; q;    vi arr(n + 1);    vi ord;    rep(i, 1, n) cin &gt;&gt; arr[i], ord.eb(arr[i]);    sort(all(ord));    rep(i, 1, n) arr[i] = (lower_bound(all(ord), arr[i])-ord.begin());    s = sqrt(n);        vii in(q);    vector&lt;Q&gt; queries(q);        for(pii &amp;i : in) cin &gt;&gt;i.F &gt;&gt; i.S;        rep(i, 0, q - 1) queries[i] = {in[i].F, in[i].S, i, 0};    sort(all(queries));    int cur_l = 1, cur_r = 1;    vi cnt(n+1, 0);    cnt[arr[1]] = 1;    int distinct = 1;    for (auto &amp;query : queries) {        while (cur_l &gt; query.l) {            cur_l--;            if (++cnt[arr[cur_l]] == 1) distinct++;        }        while (cur_r &lt; query.r) {            cur_r++;            if (++cnt[arr[cur_r]] == 1) distinct++;        }        while (cur_l &lt; query.l) {            if (--cnt[arr[cur_l]] == 0) distinct--;            cur_l++;        }        while (cur_r &gt; query.r) {            if (--cnt[arr[cur_r]] == 0) distinct--;            cur_r--;        }        query.ans = distinct;    }    vi ans(q);    for (auto &amp;query : queries) ans[query.id] = query.ans;    for (auto &amp;x : ans) cout &lt;&lt; x &lt;&lt; '\n';    return 0;}
