Forest Queries II#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */// #define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int n, q;bool arr[1003][1003];struct BIT {    int bit[1003];    void modify(int pos, int val) {        for (; pos &lt;= n; pos += pos &amp; (-pos)) bit[pos] += val;    }    int query(int pos) {        int ans = 0;        for (; pos &gt; 0; pos -= pos &amp; (-pos)) ans += bit[pos];        return ans;    }};struct BIT2 {    BIT bit[1003];    void modify(int pos_y, int pos_x, int val) {        for (; pos_y &lt;= n; pos_y += pos_y &amp; (-pos_y))            bit[pos_y].modify(pos_x, val);    }    int query(int pos_y, int pos_x) {        int ans = 0;        for (; pos_y &gt; 0; pos_y -= pos_y &amp; (-pos_y))            ans += bit[pos_y].query(pos_x);        return ans;    }};signed main() {    ios;    cin &gt;&gt; n &gt;&gt; q;    BIT2 bit;    char tmp;    rep(i, 1, n) rep(j, 1, n) cin &gt;&gt; tmp,        arr[i][j] = (tmp == '*'),        bit.modify(i, j, arr[i][j]);    while (q--) {        int cmd, y1, x1, y2, x2;        cin &gt;&gt; cmd &gt;&gt; y1 &gt;&gt; x1;        if (cmd == 1)            bit.modify(y1, x1, arr[y1][x1] == 1 ? -1 : 1), arr[y1][x1] = (arr[y1][x1])^(1);        else {            cin &gt;&gt; y2 &gt;&gt; x2;            if (x1 &lt; x2) swap(x1, x2);            if (y1 &lt; y2) swap(y1, y2);            cout &lt;&lt; bit.query(y1, x1) - bit.query(y1, x2 - 1) -                        bit.query(y2 - 1, x1) + bit.query(y2 - 1, x2 - 1)                 &lt;&lt; '\n';        }    }    return 0;}
Range Updates and Sums 
Range Updates and Sums#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si _set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)#define lc id * 2 + 1#define rc id * 2 + 2#define M ((L + R) / 2)struct Nodes {    int info, add, _set;} seg[4 * 200005];void push(int L, int R, int id) {    if (seg[id]._set) {        if (R != L + 1) seg[lc]._set = seg[rc]._set = seg[id]._set;        if (R != L + 1) seg[lc].add = seg[rc].add = 0;        seg[id].info = seg[id]._set * (R - L);        seg[id]._set = 0;    }    if (seg[id].add) {        if (R != L + 1) seg[lc].add += seg[id].add;        if (R != L + 1) seg[rc].add += seg[id].add;        seg[id].info += seg[id].add * (R - L);        seg[id].add = 0;    }}void pull(int L, int R, int id) {    push(L, M, lc), push(M, R, rc);    seg[id].info = seg[lc].info + seg[rc].info;}void add(int l, int r, int val, int L, int R, int id) {    push(L, R, id);    if (l == L &amp;&amp; R == r) {        seg[id].add += val;    } else {        if (l &gt;= M)            add(l, r, val, M, R, rc);        else if (r &lt;= M)            add(l, r, val, L, M, lc);        else            add(l, M, val, L, M, lc), add(M, r, val, M, R, rc);        pull(L, R, id);    }}void _set(int l, int r, int val, int L, int R, int id) {    push(L, R, id);    if (l == L &amp;&amp; R == r) {        seg[id]._set = val;    } else {        if (l &gt;= M)            _set(l, r, val, M, R, rc);        else if (r &lt;= M)            _set(l, r, val, L, M, lc);        else            _set(l, M, val, L, M, lc), _set(M, r, val, M, R, rc);        pull(L, R, id);    }}int query(int l, int r, int L, int R, int id) {    push(L, R, id);    if (l == L &amp;&amp; r == R) return seg[id].info;    if (l &gt;= M) return query(l, r, M, R, rc);    if (r &lt;= M) return query(l, r, L, M, lc);    return query(l, M, L, M, lc) + query(M, r, M, R, rc);}signed main() {    ios;    int n, q, tmp;    cin &gt;&gt; n &gt;&gt; q;    rep(i, 0, n - 1) cin &gt;&gt; tmp, _set(i, i + 1, tmp, 0, n, 0);    while (q--) {        int cmd, a, b, x;        cin &gt;&gt; cmd &gt;&gt; a &gt;&gt; b;        a--;        if (cmd == 1) cin &gt;&gt; x, add(a, b, x, 0, n, 0);        if (cmd == 2) cin &gt;&gt; x, _set(a, b, x, 0, n, 0);        if (cmd == 3) cout &lt;&lt; query(a, b, 0, n, 0) &lt;&lt; '\n';    }    return 0;}
