1) cin &gt;&gt; arr[i];    build();    while (q--) {        int cmd, a, b, k, x;        cin &gt;&gt; cmd;        if (cmd == 1) {            cin &gt;&gt; k &gt;&gt; a &gt;&gt; x;            k--, a--;            modify(ls[k], a, x);        } else if (cmd == 2) {            cin &gt;&gt; k &gt;&gt; a &gt;&gt; b;            k--, a--;            cout &lt;&lt; query(ls[k], a, b) &lt;&lt; '\n';        } else {            cin &gt;&gt; k;            ls.eb(ls[--k]);        }    }    return 0;}

