Josephus Problem I#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define si set&lt;int&gt;#define mii map&lt;int, int&gt;#define tomax(a,b) (a)=max((a),(b))#define tomin(a,b) (A)=min((a),(b))#define rep(i, a, b) for(int i = a; i&lt;=b; i++)#define rev(i, a, b) for(int i = a; i&gt;=b; i++)#define pob pop_back#define pb push_back#define eb emplace_back#define all(a) a.begin(), a.end()#define rall(a) a.rbegin(), a.rend()#define ios ios::sync_with_stdio(0), cin.tie(0), cout.tie(0)signed main(){    // ios;    int n;     cin &gt;&gt; n;    list&lt;int&gt; arr;    rep(i, 1, n) arr.push_back(i);    auto idx = next(arr.begin());    while(!arr.empty()){        cout &lt;&lt; *idx &lt;&lt; ' ';        if(arr.size()==1) break;        else if(arr.size()==2) arr.erase(idx), idx=arr.begin();         else if(idx==prev(arr.end())) arr.erase(idx), idx=next(arr.begin());        else if(idx==prev(arr.end(),2)) arr.erase(idx), idx=arr.begin();        else idx = next(idx, 2), arr.erase(prev(idx, 2));    }    return 0;}
Josephus Problem II 
Josephus Problem II#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)#define lc idx * 2 + 1#define rc idx * 2 + 2int seg[1000006];int n, k;//[l,r)void build(int idx, int l, int r) {    if (r == l + 1)        seg[idx] = 1;    else {        int mid = (r + l) / 2;        build(lc, l, mid), build(rc, mid, r);        seg[idx] = seg[lc] + seg[rc];    }}void del(int idx, int l, int r, int pos) {    seg[idx]--;    int mid = (r + l) / 2;    if (r == l + 1)  return;    else if (pos &gt;= mid) del(rc, mid, r, pos);    else del(lc, l, mid, pos);}int query(int idx, int l, int r, int sum){    // cerr &lt;&lt; idx sp seg[idx] sp l sp r sp sum &lt;&lt;'\n';    int mid = (l+r)/2;    if(r==l+1) return l+sum;    else if(sum&gt;seg[lc]) return query(rc, mid, r, sum-seg[lc]);    else return query(lc, l, mid, sum);}signed main() {    ios;    cin &gt;&gt; n &gt;&gt; k;    k++;    build(0, 0, n);    int idx = 0;    rev(i, n, 1){        idx = (idx+(k%i))%i;        if(idx == 0) idx = i;        int pos = query(0, 0, n, idx);        cout &lt;&lt;  pos &lt;&lt; " ";        del(0, 0, n, pos-1);        idx--;    }    return 0;}
