Nested Ranges Check#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)signed main() {    ios;    int n, a, b;    cin &gt;&gt; n;    vector&lt;bool&gt; con(n), coned(n);    vector&lt;pair&lt;pii, int&gt;&gt; ran;    rep(i, 0, n - 1) cin &gt;&gt; a &gt;&gt; b, ran.pb({{a, b}, i});    sort(all(ran), [](pair&lt;pii, int&gt; a, pair&lt;pii, int&gt; b) {        if (a.F.F == b.F.F) return a.F.S &gt; b.F.S;        return a.F.F &lt; b.F.F;    });    int max_r = 0;    for (auto i : ran) {        if (i.F.S &gt; max_r)            max_r = i.F.S, coned[i.S] = 0;        else            coned[i.S] = 1;    }    reverse(all(ran));    int min_r = 2e9;    for (auto i : ran) {        if (i.F.S &lt; min_r)            min_r = i.F.S, con[i.S] = 0;        else            con[i.S] = 1;    }    for (bool i : con) cout &lt;&lt; i &lt;&lt; ' ';    cout &lt;&lt; '\n';    for (bool i : coned) cout &lt;&lt; i &lt;&lt; ' ';    return 0;}
