Nested Ranges Count#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)int bit[200005];void update(int n, int x, int chg){    for(;x&lt;=n; x+=(x&amp;-x)) bit[x]+=chg;}int query(int x){    int ans = 0;    for(;x&gt;=1; x-=(x&amp;-x)) ans+=bit[x];    return ans;}signed main() {    // ios;    int n, a, b;    cin &gt;&gt; n;    vi con(n), coned(n);    mii bRank;    vector&lt;pair&lt;pii, int&gt;&gt; range;    rep(i, 0, n - 1) cin &gt;&gt; a &gt;&gt; b, range.pb({{a, b}, i}), bRank[b]=1;    int nn=1;    for(auto &amp;i : bRank) i.S=nn++;    nn--;    sort(all(range), [](pair&lt;pii, int&gt; a, pair&lt;pii, int&gt; b) {        if (a.F.F == b.F.F) return a.F.S &gt; b.F.S;        return a.F.F &lt; b.F.F;    });    int cnt = 0;    for (auto i : range) {        coned[i.S] = cnt-query(bRank[i.F.S]-1);        update(nn, bRank[i.F.S], 1);        cnt++;    }    reverse(all(range));    memset(bit, 0, sizeof(bit));    for (auto i : range) {        con[i.S] = query(bRank[i.F.S]);        update(nn, bRank[i.F.S], 1);        cnt++;    }    for (int i : con) cout &lt;&lt; i &lt;&lt; ' ';    cout &lt;&lt; '\n';    for (int i : coned) cout &lt;&lt; i &lt;&lt; ' ';    return 0;}
