Room Allocation#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)signed main() {    ios;    int n, room = 0;    cin &gt;&gt; n;    vi ans(n);    vector&lt;pair&lt;pii, int&gt;&gt; d(n);    rep(i, 0, n - 1) cin &gt;&gt; d[i].F.F &gt;&gt; d[i].F.S, d[i].S = i;    sort(all(d));    priority_queue&lt;pii, vii, greater&lt;pii&gt;&gt; pq;    for (auto i : d) {        if (!pq.empty() &amp;&amp; pq.top().F &lt; i.F.F) {            ans[i.S] = pq.top().S;            pq.pop();            pq.push({i.F.S, ans[i.S]});        } else {            ++room;            pq.push({i.F.S, room});            ans[i.S] = room;        }    }    cout &lt;&lt; room &lt;&lt; '\n';    for(int i : ans) cout &lt;&lt; i &lt;&lt; ' ';    return 0;}
