Sliding Window Median#include &lt;bits/stdc++.h&gt;using namespace std;/* TYPES  */#define int long long#define pii pair&lt;int, int&gt;#define F first#define S second#define vc vector#define vi vector&lt;int&gt;#define vii vector&lt;pii&gt;#define mii map&lt;int, int&gt;#define si set&lt;int&gt;/* UTILS */#define rep(i, a, b) for (int i = a; i &lt;= b; ++i)#define rev(i, a, b) for (int i = a; i &gt;= b; --i)#define tomax(a, b) (a) = max((a), (b))#define tomin(a, b) (a) = min((a), (b))#define all(a) a.begin(), a.end()#define rall(a) (a).rbegin(), (a).rend()#define pob pop_back#define pb push_back#define eb emplace_back#define ins insert#define err(a) cerr &lt;&lt; #a &lt;&lt; ": " &lt;&lt; a &lt;&lt; "\n"#define sp &lt;&lt; " " &lt;&lt;#define ios ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0)signed main() {    ios;    int n, k, tmp;    cin &gt;&gt; n &gt;&gt; k;    vi arr;    vector&lt;set&lt;pii&gt;::iterator &gt; parr;    set&lt;pii&gt; sarr;    rep(i, 0, k - 1) cin &gt;&gt; tmp, parr.eb(sarr.insert({tmp, i}).F), arr.eb(tmp);    auto p = next(sarr.begin(), (k - 1) / 2);    cout &lt;&lt; p-&gt;F &lt;&lt; ' ';    rep(i, k, n-1) {        cin &gt;&gt; tmp;        parr.eb((sarr.insert({tmp,i})).F);        arr.eb(tmp);        if (arr[i - k] == p-&gt;F &amp;&amp; tmp &lt; p-&gt;F)            p = prev(p);        else if (arr[i - k] == p-&gt;F &amp;&amp; tmp &gt;= p-&gt;F)            p = next(p);        else if (arr[i - k] &gt; p-&gt;F &amp;&amp; tmp &lt; p-&gt;F)            p = prev(p);        else if (arr[i - k] &lt; p-&gt;F &amp;&amp; tmp &gt;= p-&gt;F)            p = next(p);        if(p==parr[i-k]) p=next(p);        sarr.erase(parr[i-k]);        cout &lt;&lt; p-&gt;F &lt;&lt; ' ';    }    return 0;}
