Kruskal's Algorithm O(N log N)#include &lt;bits/stdc++.h&gt;#define N 200005#define edge pair&lt;int, pair&lt;int, int&gt; &gt;#define u first#define v second.first#define w second.second#define int long longusing namespace std;int boss[N], n, m;edge E[N];bool cmp(edge a, edge b){    return a.w &lt; b.w;}int find_boss(int x){    if(x == boss[x])    return x;    return boss[x] = find_boss(boss[x]);  //è·¯å¾‘å£“ç¸®}signed main(){    ios::sync_with_stdio(0);    cin.tie(0);    while(cin &gt;&gt; n &gt;&gt; m){        int num_edge = 0;        for(int i = 0; i&lt;n; i++) boss[i] = i;        for(int i = 0; i&lt;m; i++) cin &gt;&gt; E[i].u &gt;&gt; E[i].v &gt;&gt; E[i].w;        sort(E, E+m, cmp);        int ans = 0;                   for(int i = 0; i&lt;m; i++){            int a = find_boss(E[i].u), b = find_boss(E[i].v);            if(a!=b){                boss[b] = a;                ans += E[i].w;                num_edge++;            }        }        if(num_edge == n-1)            cout &lt;&lt; ans &lt;&lt; '\n';        else cout &lt;&lt; "-1\n";    }        return 0;}
